    /**
     * @covers RecordsMan\Record::save
     */
    public function testSave() {
        // Adding new one test
        /** @var Item $item */
        $item = Item::create([
            'title' => 'new test item',
            'parent_id' => 1
        ]);
        $itemsCount = Item::all()->count();
        /** @var Item $firstItem */
        $firstItem = Item::load(1);
        $childrenBefore = $firstItem->children_count;
        $this->assertEquals(null, $item->id);
        $item->save();
        $this->assertEquals(8, $item->id);
        $this->assertEquals($itemsCount + 1, Item::all()->count());

        // Counters updating test
        $subitemsCount = $firstItem->subitems_count;
        $newId = SubItem::create([
            'title' => 'new test subitem',
            'item_id' => 1
        ])->save()->get('id');
        $firstItem->reload();
        $this->assertEquals($childrenBefore + 1, $firstItem->children_count);
        $this->assertEquals($subitemsCount + 1, $firstItem->subitems_count);

        // Updating test
        /** @var SubItem $newItem */
        $newItem = SubItem::load($newId);
        $this->assertEquals('new test subitem', $newItem->title);
        $newItem->setTitle('updated title')->save();
        $newItem = SubItem::load($newId);
        $this->assertEquals('updated title', $newItem->title);

        // Broken foreign key test
        /** @var Item $topLevelItem */
        $topLevelItem = Item::create([
            'title' => 'top-level item',
            'parent_id' => 0
        ]);
        $topLevelItem->save();
        $this->assertTrue($topLevelItem->id > 0);
        /** @var SubSubItem $brokenItem */
        $brokenItem = SubSubItem::create([
            'title' => 'broken item',
            'subitem_id' => 100
        ]);
        $brokenItem->save(false);
        $this->assertTrue($brokenItem->id > 0);
        $brokenItem->drop();
        $this->setExpectedException('RecordsMan\RecordsManException', '', 85);
        $brokenItem->save();

        //TODO: relation checking, autofill datetime fields, etc.
    }

    /**
     * @covers RecordsMan\Record::drop
     */
    public function testDrop() {
        // Drop only test
        $itemsCount = Item::all()->count();
        /** @var Item $item */
        $item = Item::load(8);
        $item->drop();
        $this->assertEquals('new test item', $item->title);
        $this->assertTrue(!$item->id);
        $this->assertEquals($itemsCount - 1, Item::all()->count());

        // Auto-counting test
        /** @var SubItem $subitem */
        $subitem = SubItem::load(1);
        $parentItem = $subitem->item;
        $countBefore = $parentItem->subitems_count;
        $subitem->drop();
        $parentItem->reload();
        $this->assertEquals($countBefore - 1, $parentItem->subitems_count);

        // Related item deleting test
        $this->assertFalse(SubItem::find('item_id = 2')->isEmpty());
        $this->assertFalse(Item::find('parent_id = 2')->isEmpty());
        Item::load(2)->drop();
        $this->assertTrue(SubItem::find('item_id = 2')->isEmpty());
        $this->assertTrue(Item::find('parent_id = 2')->isEmpty());
    }

    public function testAddProperty() {
        $loader = Item::getLoader();
        $loaderReflection = new \ReflectionClass($loader);
        $classesReflection = $loaderReflection->getProperty('_classes');
        $classesReflection->setAccessible(true);
        $_classes = $classesReflection->getValue($loader);
        $this->assertFalse(isset($_classes['\Test\Item']['properties']['field']['getters']));
        $this->assertFalse(isset($_classes['\Test\Item']['properties']['field']['setters']));
        Item::addProperty('field', function() {}, function() {});
        $_classes = $classesReflection->getValue($loader);
        $this->assertTrue(isset($_classes['\Test\Item']['properties']['field']['getters']));
        $this->assertTrue(isset($_classes['\Test\Item']['properties']['field']['setters']));
        $this->assertCount(1, $_classes['\Test\Item']['properties']['field']['getters']);
        $this->assertCount(1, $_classes['\Test\Item']['properties']['field']['setters']);
        Item::addProperty('field', function() {}, function() {});
        $_classes = $classesReflection->getValue($loader);
        $this->assertCount(2, $_classes['\Test\Item']['properties']['field']['getters']);
        $this->assertCount(2, $_classes['\Test\Item']['properties']['field']['setters']);
        $this->assertFalse(isset($_classes['\Test\SubItem']['properties']['field']['getters']));
        $this->assertFalse(isset($_classes['\Test\SubItem']['properties']['field']['setters']));
        SubItem::addProperty('field', function() {}, function() {});
        $_classes = $classesReflection->getValue($loader);
        $this->assertTrue(isset($_classes['\Test\SubItem']['properties']['field']['getters']));
        $this->assertTrue(isset($_classes['\Test\SubItem']['properties']['field']['setters']));
        $this->assertCount(1, $_classes['\Test\SubItem']['properties']['field']['getters']);
        $this->assertCount(1, $_classes['\Test\SubItem']['properties']['field']['setters']);
    }

    public function testGetProperty() {
        /** @var Item $item */
        $item = Item::load(1);
        Item::addProperty('id_info', function() {
            return 'Id of';
        });
        Item::addProperty('id_info', function($fieldValue) {
            return "{$fieldValue} " . get_class($this);
        });
        Item::addProperty('id_info', function($fieldValue) {
            /** @var \Test\Item $this */
            return "{$fieldValue} is {$this->id}";
        });
        $this->assertEquals('Id of Test\Item is 1', $item->id_info);
        /** @var SubItem $subItem */
        $subItem = SubItem::load(2);
        SubItem::addProperty('id_info', function() {
            return '2x id of';
        });
        SubItem::addProperty('id_info', function($fieldValue) {
            return "{$fieldValue} " . get_class($this);
        });
        SubItem::addProperty('id_info', function($fieldValue) {
            /** @var \Test\Item $this */
            return "{$fieldValue} is " . $this->id * 2;
        });
        $this->assertEquals('2x id of Test\SubItem is 4', $subItem->id_info);
    }

    public function testSetProperty() {
        /** @var Item $item */
