        $item->setterTest(2);
        $this->assertEquals(2 * 2, $item->setter_test);
        Item::addProperty('setter_test', null, function($value) {
            return "{$value} " . get_class($this);
        });
        $item->setterTest(2);
        $this->assertEquals('4 Test\Item', $item->setter_test);
    }

